import React from "react";
import Header from "../Common/Header";
import Footer from "../Common/Footer";

function AttendanceLive() {
  // Dummy data for demonstration
  const attendanceRecords = [
    { date: "2025-09-15", subject: "Math", status: "Present" },
    { date: "2025-09-16", subject: "Science", status: "Absent" },
    { date: "2025-09-17", subject: "English", status: "Present" },
  ];

  return (
    <>
      <Header role="student" />
      <div className="min-h-screen bg-gray-100 p-6">
        <h2 className="text-2xl font-bold mb-6">Live Attendance</h2>

        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl transition-shadow">
          <table className="w-full table-auto border-collapse">
            <thead>
              <tr className="bg-green-500 text-white">
                <th className="px-4 py-2">Date</th>
                <th className="px-4 py-2">Subject</th>
                <th className="px-4 py-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {attendanceRecords.map((record, index) => (
                <tr key={index} className="text-center border-b">
                  <td className="px-4 py-2">{record.date}</td>
                  <td className="px-4 py-2">{record.subject}</td>
                  <td
                    className={`px-4 py-2 font-bold ${
                      record.status === "Present" ? "text-green-600" : "text-red-600"
                    }`}
                  >
                    {record.status}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default AttendanceLive;
